settings.cardCss:
.status-panel {
  background: #fff;
  padding: 1rem;
}

.main-title {
  font-size: 20px;
  font-weight: 600;
  margin: 0 0 1rem;
  text-align: center;
}

.status-container {
  display: grid;
  gap: 4.0rem;
  grid-template-columns: repeat(3, 1fr);
}

@media (max-width: 600px) {
  .status-container {
    grid-template-columns: repeat(2, 1fr);
  }
  .status-container > .status-item:nth-child(3) {
    grid-column: 1 / -1;
  }
}

.status-item {
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.label-text {
  margin-bottom: 1.5rem;
  font-weight: 600;
}

/* icon container */
.status-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  transition: color 0.3s, transform 0.3s;
}

.status-icon i {
  font-size: 3rem;
}

@media (max-width: 600px) {
  .status-icon i {
    font-size: 2.5rem;
  }
}

/* Animations */
@keyframes pulse {
  0%, 100% { transform: scale(1); text-shadow: none; }
  50%      { transform: scale(1.15); text-shadow: 0 0 20px currentColor; }
}

@keyframes spin {
  0%   { transform: rotate(0deg);   text-shadow: 0 0 20px currentColor; }
  50%  { transform: rotate(180deg); text-shadow: 0 0 20px currentColor; }
  100% { transform: rotate(360deg); text-shadow: 0 0 20px currentColor; }
}

/* active states */
#fans-item.active .status-icon {
  color: #28a745;
  animation: spin 4s linear infinite;
}

#refr-item.active .status-icon {
  color: #87cefa;
  animation: pulse 2s infinite ease-in-out;
}

#defr-item.active .status-icon {
