/* Variables */
:root {
  --bg: #ffffff;
  --panel-bg: #f9fafb;
  --border: #e5e7eb;
  --text: #374151;
  --muted: #6b7280;
  --accent: #22c55e;
}

/* Container */
.status-panel {
  width: 100%;
  background: var(--bg);
  border: 1px solid var(--border);
  border-radius: 8px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* Header */
.panel-header {
  padding: 20px 24px 16px;
  border-bottom: 1px solid #f3f4f6;
}
.panel-title {
  font-size: 18px;
  font-weight: 600;
  margin: 0;
  text-align: center;
  color: #111827;
}

/* Grid */
.status-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
  padding: 24px;
}

/* Item */
.status-item {
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 16px 20px;
  background: var(--panel-bg);
  border: 1px solid var(--border);
  border-radius: 8px;
  transition: background 0.2s, border-color 0.2s;
}
.status-item.active {
  background: #f0fdf4;
  border-color: var(--accent);
}

/* Indicator */
.status-indicator {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: #d1d5db;
  transition: background 0.3s, box-shadow 0.3s;
}
.status-item.active .status-indicator {
  background: var(--accent);
  box-shadow: 0 0 8px rgba(34, 197, 94, 0.4);
}

/* Icon */
.status-icon {
  color: var(--text);
  font-size: 48px;
  flex-shrink: 0;
  transition: color 0.2s;
}
.status-item.active .status-icon {
  color: var(--accent);
}

/* Label & status */
.status-label {
  flex: 1;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.label-text {
  font-size: 15px;
  font-weight: 500;
  color: var(--text);
}
.status-text {
  font-size: 13px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: var(--muted);
  padding: 4px 8px;
  background: var(--bg);
  border: 1px solid var(--border);
  border-radius: 4px;
  transition: color 0.2s, background 0.2s, border-color 0.2s;
}
.status-item.active .status-text {
  color: #166534;
  background: #dcfce7;
  border-color: #bbf7d0;
}

/* Responsive */
@media (max-width: 768px) {
  .status-grid {
    grid-template-columns: 1fr;
    padding: 16px;
  }
  .panel-header {
    padding: 16px 20px 12px;
  }
}
