:root {
    --bg: #0a1611;
    --panel-bg: linear-gradient(145deg, #0f1b14, #1a2e20);
    --border: #2d4a37;
    --text: #a3b8aa;
    --muted: #7a8c7e;
    --accent: #00ff87;
    --accent-glow: rgba(0, 255, 135, 0.3);
    --accent-border: #00cc6a;
    --text-strong: #e8f5e8;
    --shadow-dark: rgba(0, 0, 0, 0.4);
    --shadow-light: rgba(255, 255, 255, 0.05);
}

* {
    box-sizing: border-box;
}

/* Main panel */
.status-panel {
    width: 100%;
    max-width: 600px;
    background: var(--panel-bg);
    border-radius: 20px;
    border: 1px solid var(--border);
    box-shadow: 
        0 25px 50px var(--shadow-dark),
        inset 0 1px 0 var(--shadow-light);
    overflow: hidden;
    backdrop-filter: blur(10px);
    margin: 0 auto;
}

/* Header */
.panel-header {
    padding: 30px 32px 24px;
    border-bottom: 1px solid var(--border);
    background: linear-gradient(135deg, rgba(0, 255, 135, 0.05), rgba(0, 255, 135, 0.02));
    position: relative;
}

.panel-header::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, var(--accent), transparent);
    opacity: 0.6;
}

.main-title {
    color: var(--text-strong);
    font-size: 24px;
    font-weight: 700;
    margin: 0;
    text-align: center;
    letter-spacing: -0.025em;
    text-shadow: 0 2px 4px var(--shadow-dark);
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* Grid */
.status-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
    gap: 32px;
    padding: 36px 32px;
    justify-items: center;
}

/* Status Item */
.status-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 16px;
    transition: transform 0.3s ease;
}

.status-item:hover {
    transform: translateY(-4px);
}

/* Label */
.label-text {
    font-size: 16px;
    font-weight: 600;
    color: var(--text);
    letter-spacing: 0.025em;
    text-transform: uppercase;
    margin-bottom: 4px;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* Circle with icon */
.status-circle {
    width: 120px;
    height: 120px;
    border-radius: 50%;
    background: linear-gradient(145deg, #1a2e20, #0f1b14);
    border: 2px solid var(--border);
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 
        0 8px 25px var(--shadow-dark),
        inset 0 1px 0 var(--shadow-light);
}

.status-item.active .status-circle {
    background: linear-gradient(145deg, rgba(0, 255, 135, 0.15), rgba(0, 255, 135, 0.05));
    border-color: var(--accent);
    box-shadow: 
        0 0 30px var(--accent-glow),
        0 0 60px rgba(0, 255, 135, 0.15),
        0 8px 25px var(--shadow-dark),
        inset 0 1px 0 var(--shadow-light);
    animation: pulse-glow 2s ease-in-out infinite;
}

@keyframes pulse-glow {
    0%, 100% {
        box-shadow: 
            0 0 30px var(--accent-glow),
            0 0 60px rgba(0, 255, 135, 0.15),
            0 8px 25px var(--shadow-dark),
            inset 0 1px 0 var(--shadow-light);
    }
    50% {
        box-shadow: 
            0 0 40px var(--accent-glow),
            0 0 80px rgba(0, 255, 135, 0.25),
            0 12px 30px var(--shadow-dark),
            inset 0 1px 0 var(--shadow-light);
    }
}

.status-icon {
    color: var(--muted);
    transition: all 0.4s ease;
    filter: drop-shadow(0 2px 4px var(--shadow-dark));
}

.status-item.active .status-icon {
    color: var(--accent);
    filter: 
        drop-shadow(0 0 8px var(--accent-glow))
        drop-shadow(0 2px 4px var(--shadow-dark));
    animation: icon-pulse 2s ease-in-out infinite;
}

@keyframes icon-pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

/* Icon size overrides */
.icon-size-sm i { font-size: 28px; }
.icon-size-md i { font-size: 36px; }
.icon-size-lg i { font-size: 44px; }
.icon-size-xl i { font-size: 52px; }

/* Status text inside circle */
.status-text {
    position: absolute;
    bottom: 10px;
    font-size: 11px;
    font-weight: 700;
    color: var(--accent);
    text-transform: uppercase;
    letter-spacing: 1px;
    display: none;
    text-shadow: 0 0 8px var(--accent-glow);
    animation: text-glow 2s ease-in-out infinite;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

@keyframes text-glow {
    0%, 100% { 
        text-shadow: 0 0 8px var(--accent-glow);
        opacity: 0.9;
    }
    50% { 
        text-shadow: 0 0 12px var(--accent-glow);
        opacity: 1;
    }
}

.status-item.active .status-text {
    display: block;
}

/* Additional visual enhancements */
.status-circle::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    border-radius: 50%;
    background: linear-gradient(45deg, transparent, var(--accent), transparent);
    opacity: 0;
    transition: opacity 0.4s ease;
}

.status-item.active .status-circle::before {
    opacity: 0.3;
    animation: rotate-border 3s linear infinite;
}

@keyframes rotate-border {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

/* Enhanced mobile responsiveness */
@media (max-width: 768px) {
    .status-grid {
        grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
        gap: 24px;
        padding: 24px 20px;
    }

    .status-circle {
        width: 100px;
        height: 100px;
    }

    .icon-size-xl i { font-size: 44px; }

    .panel-header {
        padding: 24px 20px 20px;
    }

    .main-title {
        font-size: 20px;
    }

    .label-text {
        font-size: 14px;
    }
}

@media (max-width: 480px) {
    .status-grid {
        grid-template-columns: 1fr;
        gap: 20px;
        padding: 20px 16px;
    }

    .status-circle {
        width: 90px;
        height: 90px;
    }

    .icon-size-xl i { font-size: 36px; }

    .panel-header {
        padding: 20px 16px 16px;
    }

    .main-title {
        font-size: 18px;
    }
}

@media (max-width: 320px) {
    .status-grid {
        padding: 16px 12px;
    }

    .status-circle {
        width: 80px;
        height: 80px;
    }

    .icon-size-xl i { font-size: 32px; }
}
