headerButton.customFunction:
// This sends an attribute update with total_accumulated_time = 0
async function($event, widgetContext, entityId, entityName, additionalParams, entityLabel) {
    const attr = {
        total_accumulated_time: "0",
        session_start_time: "0",
        previous_state: "0",
        last_update_time: "0"
    };

    // Send attributes to device or asset
    await widgetContext.attributeService.saveEntityAttributes(entityId, 'SHARED_SCOPE', attr);

    // Optional: show notification
    widgetContext.showSuccessToast('Compressor time reset!');
}
