settings.cardCss:
.card {
    width: 100%;
    height: 100%;
    background: white;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    border: 1px solid #d1d5db;
}

.card .content {
    padding: 24px;
    height: 100%;
    box-sizing: border-box;
}

.stats-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 16px;
    margin-top: 20px;
}

.stat-box {
    background: #ffffff;
    border-radius: 6px;
    padding: 20px 16px;
    border: 1px solid #e5e7eb;
    text-align: center;
}

.stat-title {
    font-size: 13px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    margin: 0 0 12px 0;
    color: #6b7280;
}

.stat-value {
    font-size: 24px;
    font-weight: 600;
    margin: 0;
    color: #111827;
}

.main-title {
    color: #111827;
    font-size: 20px;
    font-weight: 600;
    margin: 0;
    text-align: center;
}

.compressor-box { 
    border-left: 3px solid #27ae60; 
}

.usage-box { 
    border-left: 3px solid #dc2626; 
}

.compressor-title { 
    color: #27ae60; 
}

.usage-title { 
    color: #dc2626; 
}

