@media (max-width: 480px) {
    .compressor-form {
        min-width: 320px;
    }

    .time-inputs {
        flex-direction: column;
        gap: 8px;
    }

    .time-field {
        width: 100%;
    }

    .compressor-form mat-dialog-actions {
        flex-direction: column;
        gap: 8px;
    }

    .compressor-form mat-dialog-actions button {
        width: 100%;
    }
}

