const $injector = widgetContext.$scope.$injector;
const customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
const dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

function createAlertModal(title, message, type = 'info') {
    const template = `
        <div>
            <mat-toolbar color="primary">
                <h2>${title}</h2>
                <span class="flex-1"></span>
                <button mat-icon-button (click)="close()" type="button">
                    <mat-icon>close</mat-icon>
                </button>
            </mat-toolbar>

            <div mat-dialog-content style="padding: 24px;">
                <div style="text-align: center; padding: 20px;">
                    <div style="font-size: 16px; color: #333;">${message}</div>
                </div>
            </div>

            <div mat-dialog-actions style="padding: 16px 24px; justify-content: flex-end;">
                <button mat-raised-button color="primary" (click)="close()">
                    OK
                </button>
            </div>
        </div>
    `;

    return template;
}

function AlertController(instance) {
    const vm = instance;
    vm.close = () => vm.dialogRef.close(null);
    vm.cancel = () => vm.dialogRef.close(null);
}

function showCustomAlert(title, message, type = 'info') {
    const alertTemplate = createAlertModal(title, message, type);
    customDialog.customDialog(alertTemplate, AlertController).subscribe();
}

function openCompressorTimeDialog() {
    customDialog.customDialog(htmlTemplate, CompressorTimeDialogController).subscribe();
}

function CompressorTimeDialogController(instance) {
    const vm = instance;

    vm.compressorFormGroup = vm.fb.group({
        action: ['modify'],
        hours: [0, [vm.validators.min(0), vm.validators.max(8760)]],
        minutes: [0, [vm.validators.min(0), vm.validators.max(59)]],
        seconds: [0, [vm.validators.min(0), vm.validators.max(59)]]
    });

    vm.actionOptions = [
        { value: 'modify', label: 'Modify Initial Compressor Time' },
        { value: 'reset', label: 'Reset All Times to Zero' }
    ];

    vm.cancel = () => vm.dialogRef.close(null);

    vm.save = () => {
        if (!vm.compressorFormGroup.valid) return;

        const formValues = vm.compressorFormGroup.value;
        formValues.action === 'reset' ? resetAllTimes() : modifyInitialTime(formValues);
    };

    function resetAllTimes() {
        const attributes = [
            { key: "total_uptime_start", value: Date.now().toString() },
            { key: "total_compressor_time", value: "0" },
            { key: "initial_compressor_time", value: "0" },
            { key: "session_start_time", value: "0" },
            { key: "previous_state", value: "0" },
            { key: "last_update_time", value: "0" }
        ];
        saveAttributes(attributes, 'All times reset successfully!', 'success');
    }

    function modifyInitialTime(formValues) {
        const totalMs = (formValues.hours * 3600000) + 
                       (formValues.minutes * 60000) + 
                       (formValues.seconds * 1000);
        const attributes = [{ key: "initial_compressor_time", value: totalMs.toString() }];
        saveAttributes(attributes, 'Initial compressor time updated!', 'success');
    }

    function saveAttributes(attributes, successMessage, messageType = 'success') {
        widgetContext.attributeService.saveEntityAttributes(entityId, 'SHARED_SCOPE', attributes)
            .subscribe({
                next: () => {
                    vm.dialogRef.close(null);
                    showCustomAlert('Success', successMessage, messageType);
                },
                error: () => {
                    showCustomAlert('Error', 'Operation failed', 'error');
                }
            });
    }
}

openCompressorTimeDialog();
