headerButton.customFunction:
// grab injector & services
const inj    = widgetContext.$scope.$injector;
const dialog = inj.get(widgetContext.servicesMap.get('customDialog'));
const attr   = widgetContext.attributeService;
const id     = widgetContext.entityId;

// helper: open by template-id
function openTpl(tplId, Ctrl, locals = {}) {
  const el = document.getElementById(tplId);
  if (!el) {
    console.error('Template not found:', tplId);
    return;
  }
  dialog
    .customDialog(el.innerHTML, vm => Ctrl(vm, locals))
    .subscribe();
}

// alert
function AlertCtrl(vm, { title, message }) {
  vm.title   = title;
  vm.message = message;
  vm.close   = () => vm.dialogRef.close();
}
window.showAlert = (t, m) =>
  openTpl('tb-alert-tpl', AlertCtrl, { title: t, message: m });

// compressor dialog
function CompressorCtrl(vm) {
  vm.action  = 'modify';
  vm.hours   = vm.minutes = vm.seconds = 0;
  vm.cancel  = ()       => vm.dialogRef.close();
  vm.save    = ()       => {
    const attrs = vm.action === 'reset'
      ? getResetAttrs()
      : getModifyAttrs(vm.hours, vm.minutes, vm.seconds);
    attr
      .saveEntityAttributes(id, 'SHARED_SCOPE', attrs)
      .subscribe({
        next: () => { vm.dialogRef.close(); showAlert('Success','Done'); },
        error:() => showAlert('Error','Failed')
      });
  };
}
function getResetAttrs() {
  const now = Date.now().toString();
  return [
    { key: 'total_uptime_start',      value: now },
    { key: 'total_compressor_time',   value: '0' },
    { key: 'initial_compressor_time', value: '0' },
    { key: 'session_start_time',      value: '0' },
    { key: 'previous_state',          value: '0' },
    { key: 'last_update_time',        value: '0' }
  ];
}
function getModifyAttrs(h, m, s) {
  return [{
    key:   'initial_compressor_time',
    value: String(h*3600000 + m*60000 + s*1000)
  }];
}
window.openCompressor = () =>
  openTpl('tb-compressor-tpl', CompressorCtrl);

// launch when widget loads
window.openCompressor();
