var totalMs = value;
var hours = Math.floor(totalMs / 3600000);
var minutes = Math.floor((totalMs % 3600000) / 60000);
var seconds = Math.floor((totalMs % 60000) / 1000);

if (hours > 0) {
    return hours + "h " + minutes + "m " + seconds + "s";
} else if (minutes > 0) {
    return minutes + "m " + seconds + "s";
} else {
    return seconds + "s";
}

