headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

openResetConfirmDialog();

function openResetConfirmDialog() {
    let title = 'Reset Compressor Time';
    let content = 'Are you sure you want to reset the compressor accumulated time to zero? This action cannot be undone.';
    dialogs.confirm(title, content, 'Cancel', 'Reset').subscribe(
        function(result) {
            if (result) {
                resetCompressorTime();
            }
        }
    );
}

function resetCompressorTime() {
    var attributes = [
        {"key": "total_accumulated_time", "value": "0"},
        {"key": "session_start_time", "value": "0"}, 
        {"key": "previous_state", "value": "0"},
        {"key": "last_update_time", "value": "0"}
    ];

    widgetContext.attributeService.saveEntityAttributes(
        entityId, 
        'SHARED_SCOPE',
        attributes
    ).subscribe(
        function(result) {
            dialogs.alert('Success', 'Compressor time reset successfully!', 'OK').subscribe();
        },
        function(error) {
            dialogs.alert('Error', 'Reset failed: ' + error, 'OK').subscribe();
        }
    );
}
